        ingredients : {
            salad: 1,
            bacon:1,
            cheese:2,
            meat:2

        }

    }
    render(){
        return (
            <Aux>
             <Burger ingredients={this.state.ingredients}/>
            <div>
                Build Controls
            </div>
            </Aux>
        );
    }
}

export default BurgerBuilder;
